class NaiveSearch {	static public void main(String arg[]) {				String t = "this is a long text, but may be felt short.";		String s = "ext";				System.out.println(t+"\n"+s+"\n");		int i, j, k;				for (i = 0; i < t.length(); i++) {			j = 0;			while ((j < s.length()) && (t.charAt(i+j) == s.charAt(j))) {				System.out.println(s.charAt(j));				j++;			}			if (s.length() == j) {				System.out.println(i);				return;			}		}		System.out.println("Not found.");		return;	}}